mapper.select:
select * from
(select rownum rn, tt.* from
(select * from itemtable where  itemcode like '%'||#{categoryCode }||'%' order by itemcode desc)tt
)where rn>=#{startNum} and rn <= #{endNum}

select * from
(select rownum rn, tt.* from
(select * from itembasketTable join ITEMTABLE using (itemcode) 
 where itemcode like '%'||#{itemCode}||'%' order by itembaskettable.registday desc)tt
)where rn>=#{startNum} and rn <= ${endNum}
