mapper.select:
select * from
(select rownum rn, tt.* from
(select * from itemtable where  itemcode like '%'||#{categoryCode }||'%' order by itemcode desc)tt
)where rn>=#{startNum} and rn <= #{endNum}

select *
from itembasketTable join ITEMTABLE using (itemcode)
where stus='cart' and id=#{id}
order by seq desc

select *
from itembasketTable join ITEMTABLE using (itemcode)
where stus='cart' and id=#{id}
order by seq desc
