mapper.update:
begin
	update user_table set cash=#{reCash} where id=#{id};
	delete itembasketTable where seq=#{seq};
end;

mapper.select:
select *
from itembasketTable join ITEMTABLE using (itemcode)
where stus=#{stus}
order by seq asc
